package com.bytesnapper;import java.util.ArrayList;import java.util.HashMap;import java.util.List;import java.util.Map;/** * A {@link Game} represents of bowling game * * @author Ahmed Soliman */public class Game {    private final List<Integer> balls = new ArrayList();    private final Map<Integer, Integer> scorePerFrame = new HashMap<>();    public List<Integer> getBalls() {        return balls;    }    public Map<Integer, Integer> getScorePerFrame() {        return scorePerFrame;    }    public int getTotalScore() {        return this.getScorePerFrame().values().stream().mapToInt(Integer::intValue).sum();    }}